var ValidationRule = require("../validationRule");

module.exports = class BetweenValidation extends ValidationRule
{
    constructor(value)
    {
        super("between", value);
    }

    validate(minValue, maxValue)
    {
        var isValid = this.value > minValue && this.value < maxValue;
        if(isValid) return;
        throw `${this.value} is not between ${minValue} and ${maxValue}`;
    }

}
