var ValidationRule = require("../validationRule");

module.exports = class GreaterThanValidator extends ValidationRule
{
    constructor(value)
    {
        super("greaterThan", value);
    }

    validate(maxValue)
    {
        var isValid = this.value > maxValue;
        if(isValid) return;
        throw `${this.value} is lower than max`
    }

}
