var ValidationRule = require("../validationRule");

module.exports = class LessThanValidator extends ValidationRule
{
    constructor(value)
    {
        super("lessThan", value);
    }

    validate(minValue)
    {
        var isValid = this.value < minValue;
        if(isValid) return;
        throw `${value} is bigger than ${minValue}`;
    }

}
