var ValidationProperty = require("./validationProperty");
class Validation
{
    constructor(objectToValidate)
    {
        for(var property in objectToValidate)
            this[property] = new ValidationProperty(objectToValidate[property]);
    }
}

module.exports = function getValidationProxy(objectToValidate)
{
    var validation = new Validation(objectToValidate);
    return new Proxy(validation, {
        get: function(target, property)
        {
            return target[property] == undefined? 
                new ValidationProperty(undefined) :
                target[property];
        }
    })
}
